Fisma.UrlPanel=function(){return{showPanel:function(c,b,d){var a=new YAHOO.widget.Panel("panel",{width:"540px",modal:true});a.setHeader(c);a.setBody("Loading...");a.render(document.body);a.center();a.show();if(b!=""){YAHOO.util.Connect.asyncRequest("GET",b,{success:function(e){e.argument.setBody(e.responseText);e.argument.center();if(typeof(d)=="function"){d()}},failure:function(e){alert("Failed to load the specified panel.")},argument:a},null)}else{alert("The parameter url can not be empty.")}return a}}}();