function GeneratePassword(){var a=document.getElementById("generate_password");YAHOO.util.Connect.asyncRequest("GET","/user/generate-password/format/html",{success:function(b){document.getElementById("password").value=b.responseText;document.getElementById("confirmPassword").value=b.responseText},failure:function(b){alert("Failed to generate password: "+b.statusText)}},null);return false}var check_account=function(){var b=document.getElementById("username").value;b=encodeURIComponent(b);var a="/user/check-account/format/html/account/"+b;YAHOO.util.Connect.asyncRequest("GET",a,{success:function(d){var c=YAHOO.lang.JSON.parse(d.responseText);message(c.msg,c.type)},failure:function(c){alert("Failed to generate password: "+c.statusText)}},null);return false};