var helpPanels=new Array();function showHelp(b,c){if(helpPanels[c]){helpPanels[c].show()}else{var a=new YAHOO.widget.Panel("helpPanel",{width:"400px"});a.setHeader("Help");a.setBody("Loading...");a.render(document.body);a.center();a.show();YAHOO.util.Connect.asyncRequest("GET","/help/help/module/"+c,{success:function(d){d.argument.setBody(d.responseText);d.argument.center()},failure:function(d){alert("Failed to load the help module.")},argument:a},null);helpPanels[c]=a}};